import React from 'react';

function Login(props){
    const {email,
        setEmail,
        password,
        setPassword,
        handleLogin,
        handleSignUp,
        hasAccount,
        setHasAccount,
        emailError,
        passwordError}=props;
    return (<section className="login">
        <div className="loginContainer">
            <label>Email</label>
            <input type="email"
            required
            autoFocus
            value={email}
            placeholder="Username"/>
            <label>Password</label>
            <input type="pass"
            required
            autoFocus
            value={password}
            placeholder="Password"/>
            <p></p>
            <div className="btnContainer">Sign Up</div>
        </div>
    </section>
    );
}
export default Login;
